DROP TABLE  IF EXISTS `movies`;


CREATE TABLE `movies` (
  `id` INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
  `plot` VARCHAR(1024) NULL,
  `genres` JSON NULL,
  `runtime` BIGINT NULL,
  `cast` JSON NULL,
  `poster` VARCHAR(1024) NULL,
  `title` VARCHAR(1024),
  `languages` JSON NULL,
  `directors` JSON NULL,
  `writers` VARCHAR(1024) NULL,
  `awards.text` VARCHAR(1024),
  `year` BIGINT,
  `imdb.rating` VARCHAR(1024),
  `imdb.votes` VARCHAR(1024),
  `countries` JSON NULL,
  `type` VARCHAR(1024)
);

